import React, { useState, useEffect } from "react";
import { AiOutlineBars } from "react-icons/ai";
import { RiCloseLine } from "react-icons/ri";
import { Link as ScrollLink, animateScroll as scroll } from "react-scroll";
import mobileLogo from "../../assets/SH_Logo_Small.svg";
import desktopLogo from "../../assets/SH_Logo_Black_Long.svg";
import "./Navbar.css";
import { useLocation, useNavigate } from "react-router-dom";

const Navbar = () => {
  const [showMenu, setShowMenu] = useState(false);
  const [activeSection, setActiveSection] = useState(null);
  const [logoSrc, setLogoSrc] = useState('');
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    function handleResize() {
      // Change logo source based on screen size
      if (window.innerWidth <= 768) {
        setLogoSrc(mobileLogo);
      } else {
        setLogoSrc(desktopLogo);
      }
    }

    // Set initial logo source based on screen size
    handleResize();

    // Add event listener to window resize
    window.addEventListener('resize', handleResize);

    // Cleanup on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const navigateHome = () => {
    if (location.pathname === "/") {
      scroll.scrollToTop();
    } else {
      navigate("/");
    }
  };

  const handleSetActive = (to) => {
    setActiveSection(to);
  };

  return (
    <nav className="navbar container_nav">
      <div className="logo" onClick={navigateHome}>
        <img src={logoSrc} alt="Logo" />
      </div>
      <menu>
        <ul
          className="nav-links"
          id={showMenu ? "nav-links-mobile" : "nav-links-mobile-hide"}
        >
          {/* Navigation links */}
        </ul>
      </menu>
      <div className="menu-icons" onClick={toggleMenu}>
        {showMenu ? (
          <RiCloseLine color="#fff" size={30} />
        ) : (
          <AiOutlineBars color="#fff" size={27} />
        )}
      </div>
    </nav>
  );
};

export default Navbar;
