import React, { Component } from "react";
import Slider from "react-slick";
//import { useNavigate } from "react-router-dom";

import "./Carousel.css";

export default class Carousel extends Component {
  render() {
    const { data, onlyImages } = this.props;

    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 1,
      autoplay: true,
      speed: 8000,
      autoplaySpeed: 8000,
      cssEase: "linear",
    };

    //const navigate = useNavigate();
    const handleButtonClick = (item) => {
      console.log(item);
      /*
      navigate(`/aboutthem/${id}`, {
        state: {
          item.title,
          item.body,
          item.image,
        },
      });
      */
    };

    return (
      <div id="s4h_carousel" className="carousel-background">
        <Slider {...settings}>
          {data.map((item) => (
            onlyImages ? (
            <div className="carouselCard">
              <img src={item.image} alt="Slider Image" className="carouselCard-image" />
            </div>
            ) : (
            <div className="carouselCard">
              <img src={item.image} alt="Slider Image" className="carouselCard-image" />
              <div className="carouselCard-content">
                <p className="carouselCard-text">{item.body}</p>
                <button className="carouselCard-button" onClick={() => handleButtonClick(item)}>Know more about my experience</button>
              </div>
            </div>
            )))}
        </Slider>
      </div>
    );
  }
}
